
public class J103_Exam_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int j = 1;j<=10;j++)
		{
			for(int i = 10;i>0;i--)
			{
				if(j<i)
				{
					System.out.print("-");
				}
				else
				{
					System.out.print("X");
				}
			}
			System.out.println();
		}
	}

}
